"use client"
import Link from 'next/link'
import '../globals.css'
import { usePathname } from 'next/navigation';
const navs = [
  { id: 1, title: "Home", slug: '/' },
  { id: 2, title: "Shop", slug: '/shop' },
  { id: 3, title: "Products", slug: '/products' }
]
// export const metadata = {
