export default function RootLayout({ children }) {
  const pathname = usePathname();
  console.log(pathname);
  return (
    <html lang="en">
      <body>
        <div className='flex flex-col h-screen'>
          <header className='bg-slate-100 w-full p-3'>
            <ul className='flex gap-3'>
              {
                navs.map(nav => {
                  const isActive = pathname === nav.slug;
                  console.log(isActive);

                  return <li key={nav.id}><Link className={isActive ? "font-bold text-violet-400" : ''} href={nav.slug}>{nav.title}</Link></li>
                })
              }
            </ul>
          </header>
          <main className='flex-1 w-full p-3'>{children}</main>
          <footer className='bg-blue-100 w-full p-3'>User Root Layout Footer</footer>
        </div>
      </body>
    </html >
  )
}
